INSERT INTO books (title, last_name, first_name, father_name, providers_num, statuses_num, cover_name, description)
VALUES ('Чистый код: создание, анализ и рефакторинг', 'Мартин', 'Роберт', '', 1, 1, '2d636c7c-e349-4ce9-8447-1807fd7acf52.jpg', 
		'Эта книга посвящена хорошему программированию. Она полна реальных примеров кода. Мы будем рассматривать код с различных направлений: сверху вниз, снизу вверх и даже изнутри. Прочитав книгу, вы узнаете много нового о коде. Более того, вы научитесь отличать хороший код от плохого. Вы узнаете, как писать хороший код и как преобразовать плохой код в хороший.'),
       
	   ('Большие надежды', 'Диккенс', 'Чарлз', '', 1, 1, '584ef704-82bc-4a34-a42a-b09ea044e2c0.jpg', 
	    'История Пипа – мальчишки из простой крестьянской семьи, который нежданно-негаданно получил возможность «выбиться в люди» и войти в лучшее лондонское общество, и Эстеллы, которую опекунша – полубезумная аристократка – сделала орудием своей мести, вырастив из нее роковую красавицу, разбивающую мужские сердца, завораживает читателя с первых же страниц, однако обрамляющие ее колоритные и яркие картины викторианского Лондона не уступают ей ни в остроумии, ни в обаянии.'),
	   
       ('Затерянный мир', 'Конан Дойл', 'Артур', '', 1, 1, 'a210f0fa-a0fd-4143-bfd9-b6ace48de5be.jpg', 
        'Роман «Затерянный мир» развертывает перед читателем увлекательную картину жизни Земли, какой она была десятки миллионов лет тому назад. Группа отважных, предприимчивых англичан во главе с профессором Челленджером проникает на якобы существующее сейчас в дебрях Южной Америки горное плато, где находит животных и растения, сохранившиеся с того времени, когда они действительно существовали на земле...'),
       
       ('Загадки сна', 'Полуэктов', 'Михаил', 'Гурьевич', 1, 1, 'ac4c1735-afe2-4d02-aef8-1b030027f4e8.jpg', 
        'Сон и сновидения всегда окутывала тайна, недоступная пониманию человека. Почему каждый день мы теряем контакт с внешним миром? Зачем нужно спать? Стоит ли придавать значение снам и почему люди вообще их видят? Загадкам сна искали объяснения – как мистические, так и основанные на научном подходе. Появление новейших методов изучения деятельности мозга, современные возможности генетических и биохимических исследований позволили узнать, почему, как и сколько мы спим на самом деле. О том, что сегодняшняя наука знает о сне, – а также о таких состояниях, как бессонница, летаргический сон, осознанные сновидения и многое другое, – и рассказывается в этой книге.'),
       
       ('Американская трагедия', 'Драйзер', 'Теодор', '', 1, 1, 'ba4a8fbe-bf84-44d5-96a0-5d152b9f5d87.jpg', 
        'Роман Теодора Драйзера «Американская трагедия», поводом для написания которого стали несколько убийств, произошедших в конце 19 – начале 20 веков, был опубликован в ноябре 1925 года. Клайд Грифитс – юноша из бедной фанатичной семьи миссионеров, который пытается получше устроиться в жизни, следуя за американской мечтой. Он тщеславен, честолюбив, страстно желает достичь красивой и богатой жизни, вершин общественного и материального благополучия. Он готов любой ценой устранить препятствие, которое стоит на его пути к богатству, роскоши и счастливому браку.');